mapper.insert:
INSERT INTO TB_TRADING_INFO
( ticker
, stock_name
, trading_count
, trading_type
, trading_price
, currency
, trading_date
, account_num
, status
, descr)
VALUES ( #{ticker}
       , ''
       , #{trading_count}
       , #{trading_type}
       , #{trading_price}
       , #{currency}
       , #{trading_date}
       , #{account_num}
       , 1
       , NULL)

mapper.update:
UPDATE TB_TRADING_INFO
SET
  trading_count = #{trading_count}
  , trading_type = #{trading_type}
  , trading_price = #{trading_price}
  , currency = #{currency}
  , trading_date = #{trading_date}
WHERE
  trading_num = #{trading_num}
